import React, { useEffect, useState } from 'react';
import axiosInstance from '../api/axiosConfig';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [fetchError, setFetchError] = useState('');

  const fetchUsers = async () => {
    try {
      const response = await axiosInstance.get('/admin/users');
      setUsers(response.data);
    } catch (error) {
      setFetchError('Failed to fetch users.');
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div className="user-list">
      <h2 style={{ marginTop: '20px' }}>All Users</h2>
      {fetchError && <p className="error-message">{fetchError}</p>}
      {users.length === 0 ? (
        <p>No users found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              {/* If you want an avatar column, you need to store an image URL, or use placeholders */}
              {/* <th>Avatar</th> */}
              <th>Name</th>
              <th>Role</th>
              <th>Token Balance</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                {/* <td><img src={someAvatarURL} alt="avatar" /></td> */}
                <td>{user.username}</td>
                <td>{user.role}</td>
                <td style={{ fontWeight: 'bold', color: '#2a9d8f' }}>
                  {user.tokenBalance}
                </td>
                <td>
                  {/* Optionally add a link or button for editing */}
                  <a href="#edit" style={{ color: '#2f3e46' }}>
                    Edit
                  </a>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default UserList;
