import React, { useEffect, useState } from 'react';
import axiosInstance from '../api/axiosConfig';
import UpdateTokensForm from './UpdateTokensForm';
import TokenBalance from './TokenBalance';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [fetchError, setFetchError] = useState('');

  const fetchUsers = async () => {
    try {
      const response = await axiosInstance.get('/admin/users');
      setUsers(response.data);
    } catch (error) {
      setFetchError('Failed to fetch users.');
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleTokenUpdate = (updatedUser) => {
    setUsers((prevUsers) =>
      prevUsers.map((user) => (user._id === updatedUser._id ? updatedUser : user))
    );
  };

  return (
    <div className="user-list">
      <h2>All Users</h2>
      {fetchError && <p className="error-message">{fetchError}</p>}
      {users.length === 0 ? (
        <p>No users found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Token Balance</th>
              <th>Update Tokens</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td>{user.username}</td>
                <td><TokenBalance balance={user.tokenBalance} /></td>
                <td>
                  <UpdateTokensForm userId={user._id} onUpdate={handleTokenUpdate} />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default UserList;
