import React, { useEffect, useState } from 'react';
import axiosInstance from '../api/axiosConfig';
import Sidebar from '../components/Sidebar';
import { FaUsers, FaUserShield } from 'react-icons/fa'; // icons for user & admin counts

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [fetchError, setFetchError] = useState('');

  // Separate counts
  const [userCount, setUserCount] = useState(0);
  const [adminCount, setAdminCount] = useState(0);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axiosInstance.get('/admin/users');
        setUsers(response.data);

        // Count by role
        const allUsers = response.data;
        const totalUsers = allUsers.filter(u => u.role === 'user').length;
        const totalAdmins = allUsers.filter(u => u.role === 'admin').length;
        setUserCount(totalUsers);
        setAdminCount(totalAdmins);
      } catch (error) {
        setFetchError('Failed to fetch users.');
      }
    };
    fetchUsers();
  }, []);

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="dashboard-content">
        <h1>Dashboard</h1>

        {/* Cards showing user & admin counts */}
        <div className="stats-grid">
          {/* Card 1: Users */}
          <div className="stats-card">
            <div className="stats-icon purple-bg">
              <FaUsers />
            </div>
            <div className="stats-text">
              <h3>{userCount}</h3>
              <span>Users</span>
            </div>
          </div>

          {/* Card 2: Admins */}
          <div className="stats-card">
            <div className="stats-icon pink-bg">
              <FaUserShield />
            </div>
            <div className="stats-text">
              <h3>{adminCount}</h3>
              <span>Admins</span>
            </div>
          </div>
        </div>

        {/* User list table */}
        <div className="user-list">
          <h2>All Users</h2>
          {fetchError && <p className="error-message">{fetchError}</p>}
          {users.length === 0 ? (
            <p>No users found.</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Role</th>
                  <th>Token Balance</th>
                </tr>
              </thead>
              <tbody>
                {users.map((u) => (
                  <tr key={u._id}>
                    <td>{u.username}</td>
                    <td>{u.role}</td>
                    <td>{u.tokenBalance}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
